// src/App.tsx
import React, { useState } from 'react';
import MatrixTable from './components/MatrixTable/MatrixTable';
import { Cell } from './type/type';
import { generateMatrix } from './utils/generateMatrix';


const App: React.FC = () => {
  const [matrix, setMatrix] = useState<Cell[][]>([]);

  const handleGenerateMatrix = () => {
    const M = 5; // Example value, replace with user input
    const N = 4; // Example value, replace with user input
    const newMatrix = generateMatrix(M, N);
    setMatrix(newMatrix);
  };

  const handleCellUpdate = (rowIndex: number, colIndex: number, newAmount: number) => {
    setMatrix(prevMatrix => {
      const newMatrix = [...prevMatrix];
      newMatrix[rowIndex] = [...newMatrix[rowIndex]];
      newMatrix[rowIndex][colIndex] = { ...newMatrix[rowIndex][colIndex], amount: newAmount };
      return newMatrix;
    });
  };

  return (
    <div>
      <button onClick={handleGenerateMatrix}>Generate Matrix</button>
      {matrix.length > 0 && <MatrixTable matrix={matrix} onCellUpdate={handleCellUpdate} />}
    </div>
  );
};

export default App;
