// src/components/MatrixTable.tsx
import React, { useState, useCallback } from 'react';
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, TableFooter, Paper } from '@mui/material';
import { Cell } from '../../type/type';
import { calculateMedian } from '../../utils/calculateMedian';

interface MatrixTableProps {
  matrix: Cell[][];
  onCellUpdate: (rowIndex: number, colIndex: number, newAmount: number) => void;
}

const MatrixTable: React.FC<MatrixTableProps> = ({ matrix, onCellUpdate }) => {
  const [hoveredCell, setHoveredCell] = useState<Cell | null>(null);
  const [nearestCells, setNearestCells] = useState<Set<number>>(new Set());

  const numRows = matrix.length;
  const numCols = matrix[0]?.length || 0;

  const calculateRowSum = useCallback((row: Cell[]): number => {
    return row.reduce((sum, cell) => sum + cell.amount, 0);
  }, []);

  const extractColumnValues = useCallback((matrix: Cell[][], columnIndex: number): number[] => {
    return matrix.map(row => row[columnIndex]?.amount || 0);
  }, []);

  const calculateColumnMedians = useCallback((): number[] => {
    const medians: number[] = [];
    for (let col = 0; col < numCols; col++) {
      const columnValues = extractColumnValues(matrix, col);
      medians.push(calculateMedian(columnValues));
    }
    return medians;
  }, [matrix, numCols, extractColumnValues]);

  const findNearestCells = useCallback((cell: Cell, X: number): void => {
    const allCells = matrix.flat();
    const sortedCells = allCells
      .map(c => ({ ...c, distance: Math.abs(c.amount - cell.amount) }))
      .sort((a, b) => a.distance - b.distance)
      .slice(0, X);

    setNearestCells(new Set(sortedCells.map(c => c.id)));
  }, [matrix]);

  const handleCellClick = (rowIndex: number, colIndex: number) => {
    const newAmount = matrix[rowIndex][colIndex].amount + 1;
    onCellUpdate(rowIndex, colIndex, newAmount);
  };

  const handleCellMouseEnter = (cell: Cell) => {
    setHoveredCell(cell);
    findNearestCells(cell, 5); // Highlight 5 nearest cells, adjust as needed
  };

  const handleCellMouseLeave = () => {
    setHoveredCell(null);
    setNearestCells(new Set());
  };

  const columnMedians = calculateColumnMedians();

  return (
    <TableContainer component={Paper}>
      <Table>
        <TableHead>
          <TableRow>
            {Array.from({ length: numCols }, (_, colIndex) => (
              <TableCell key={colIndex}>Cell Value N = {colIndex + 1}</TableCell>
            ))}
            <TableCell>Row Sum</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {matrix.map((row, rowIndex) => (
            <TableRow key={rowIndex}>
              {row.map((cell, colIndex) => (
                <TableCell
                  key={cell.id}
                  onClick={() => handleCellClick(rowIndex, colIndex)}
                  onMouseEnter={() => handleCellMouseEnter(cell)}
                  onMouseLeave={handleCellMouseLeave}
                  style={{
                    backgroundColor: nearestCells.has(cell.id) ? 'yellow' : 'transparent'
                  }}
                >
                  {cell.amount}
                </TableCell>
              ))}
              <TableCell>{calculateRowSum(row)}</TableCell>
            </TableRow>
          ))}
        </TableBody>
        <TableFooter>
          <TableRow>
            {columnMedians.map((median, colIndex) => (
              <TableCell key={colIndex}>{median}</TableCell>
            ))}
            <TableCell>Median</TableCell>
          </TableRow>
        </TableFooter>
      </Table>
    </TableContainer>
  );
};

export default MatrixTable;
