import React, { useState } from 'react';
import MatrixTable from './components/MatrixTable/MatrixTable';
import { Cell } from './type/type';
import { generateMatrix } from './utils/generateMatrix';

const App: React.FC = () => {
  const [matrix, setMatrix] = useState<Cell[][]>(generateMatrix(5, 5));

  const handleCellUpdate = (rowIndex: number, colIndex: number, newAmount: number) => {
    setMatrix(prevMatrix => {
      const newMatrix = prevMatrix.map((row, rIdx) =>
        row.map((cell, cIdx) => {
          if (rIdx === rowIndex && cIdx === colIndex) {
            return { ...cell, amount: newAmount };
          }
          return cell;
        })
      );
      return newMatrix;
    });
  };

  const handleRemoveRow = (rowIndex: number) => {
    setMatrix(prevMatrix => prevMatrix.filter((_, index) => index !== rowIndex));
  };

  const handleRemoveCell = (rowIndex: number, colIndex: number) => {
    setMatrix(prevMatrix => {
      const newMatrix = prevMatrix.map((row, rIdx) =>
        rIdx === rowIndex
          ? row.filter((_, cIdx) => cIdx !== colIndex)
          : row
      ).filter(row => row.length > 0); // Remove rows if they become empty
      return newMatrix;
    });
  };

  const handleRemoveColumn = (colIndex: number) => {
    setMatrix(prevMatrix => prevMatrix.map(row => 
      row.filter((_, cIdx) => cIdx !== colIndex)
    ));
  };

  return (
    <div>
      <MatrixTable
        matrix={matrix}
        onCellUpdate={handleCellUpdate}
        onRemoveCell={handleRemoveCell}
        onRemoveRow={handleRemoveRow}
        onRemoveColumn={handleRemoveColumn} // Pass column removal handler
      />
    </div>
  );
};

export default App;
