import React from 'react';
import { TableRow, TableCell, IconButton } from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import { Cell } from '../../../../type/type';

interface MatrixRowProps {
  row: Cell[];
  rowIndex: number;
  rowSum: number;
  hoveredSumRowIndex: number | null;
  nearestCells: Set<number>;
  onCellClick: (rowIndex: number, colIndex: number) => void;
  onCellMouseEnter: (cell: Cell) => void;
  onCellMouseLeave: () => void;
  onRemoveCell: (rowIndex: number, colIndex: number) => void;
  onRemoveRow: (rowIndex: number) => void;
}

const MatrixRow: React.FC<MatrixRowProps> = ({
  row,
  rowIndex,
  rowSum,
  hoveredSumRowIndex,
  nearestCells,
  onCellClick,
  onCellMouseEnter,
  onCellMouseLeave,
  onRemoveCell,
  onRemoveRow
}) => (
  <TableRow key={rowIndex}>
    {row.map((cell, colIndex) => {
      const percentage = rowSum ? (cell.amount / rowSum) * 100 : 0;
      const isHoveredCell = cell.id === hoveredSumRowIndex;
      const isHoveredRow = hoveredSumRowIndex === rowIndex;
      return (
        <TableCell
          key={cell.id}
          onClick={() => onCellClick(rowIndex, colIndex)}
          onMouseEnter={() => onCellMouseEnter(cell)}
          onMouseLeave={onCellMouseLeave}
          style={{
            backgroundColor: nearestCells.has(cell.id) ? 'yellow' : 'transparent',
            backgroundImage: isHoveredRow ? `linear-gradient(to right, #00f ${percentage}%, #fff ${percentage}%)` : undefined,
            position: 'relative',
            padding: 0,
            color: cell.amount === 0 ? 'transparent' : 'inherit'
          }}
        >
          {isHoveredRow ? `${percentage.toFixed(2)}%` : (cell.amount === 0 ? '-' : cell.amount)}
          {isHoveredCell && (
            <IconButton
              onClick={(e) => {
                e.stopPropagation();
                onRemoveCell(rowIndex, colIndex);
              }}
              color="secondary"
              style={{
                position: 'absolute',
                top: 0,
                right: 0,
                zIndex: 1,
              }}
            >
              <DeleteIcon />
            </IconButton>
          )}
        </TableCell>
      );
    })}
    <TableCell
      onMouseEnter={() => onCellMouseEnter(row[0])}
      onMouseLeave={onCellMouseLeave}
    >
      {rowSum}
    </TableCell>
    <TableCell>
      <IconButton
        onClick={() => onRemoveRow(rowIndex)}
        color="secondary"
        style={{
          position: 'relative',
          zIndex: 1,
        }}
      >
        <DeleteIcon />
      </IconButton>
    </TableCell>
  </TableRow>
);

export default MatrixRow;
