import React, { useEffect, useState } from "react";
import { generateMatrix } from "../../utils/generateMatrix";
import MatrixTable from "../MatrixTable/MatrixTable";

// Define the structure of a Cell
export type Cell = {
  id: number;
  amount: number;
};

interface MatrixComponentProps {
  M: number; // Number of rows
  N: number; // Number of columns
}

const MatrixComponent: React.FC<MatrixComponentProps> = ({ M, N }) => {
  const [matrix, setMatrix] = useState<Cell[][]>([]);

  useEffect(() => {
    const generatedMatrix = generateMatrix(M, N);
    setMatrix(generatedMatrix);
  }, [M, N]);

  return (
    <div>
      <h2>Matrix {M}x{N}</h2>
      <MatrixTable matrix={matrix} /> {/* Pass the matrix to MatrixTable */}
    </div>
  );
};

export default MatrixComponent;
