import React from 'react';
import { Table, TableBody, TableFooter, TableCell, TableContainer, Paper, TableRow } from '@mui/material';
import { Cell } from '../../type/type';
import { calculateColumnMedians, calculateRowSum } from './utils';
import MatrixHeader from './components/MatrixHeader/MatrixHeader';
import MatrixRow from './components/MatrixRow/MatrixRow';

interface MatrixTableProps {
  matrix: Cell[][];
  onCellUpdate: (rowIndex: number, colIndex: number, newAmount: number) => void;
  onRemoveCell: (rowIndex: number, colIndex: number) => void;
  onRemoveRow: (rowIndex: number) => void;
  onRemoveColumn: (colIndex: number) => void;
}

const MatrixTable: React.FC<MatrixTableProps> = ({
  matrix, onCellUpdate, onRemoveCell, onRemoveRow, onRemoveColumn
}) => {
  const [hoveredCell, setHoveredCell] = React.useState<Cell | null>(null);
  const [nearestCells, setNearestCells] = React.useState<Set<number>>(new Set());
  const [hoveredSumRowIndex, setHoveredSumRowIndex] = React.useState<number | null>(null);

  const numRows = matrix.length;
  const numCols = matrix[0]?.length || 0;

  const findNearestCells = React.useCallback((cell: Cell, X: number): void => {
    const allCells = matrix.flat();
    const sortedCells = allCells
      .map(c => ({ ...c, distance: Math.abs(c.amount - cell.amount) }))
      .sort((a, b) => a.distance - b.distance)
      .slice(0, X);

    setNearestCells(new Set(sortedCells.map(c => c.id)));
  }, [matrix]);

  const handleCellClick = (rowIndex: number, colIndex: number) => {
    const newAmount = matrix[rowIndex][colIndex].amount + 1;
    onCellUpdate(rowIndex, colIndex, newAmount);
  };

  const handleCellMouseEnter = (cell: Cell) => {
    setHoveredCell(cell);
    findNearestCells(cell, 5);
  };

  const handleCellMouseLeave = () => {
    setHoveredCell(null);
    setNearestCells(new Set());
  };

  const columnMedians = calculateColumnMedians(matrix);

  return (
    <TableContainer component={Paper}>
      <Table>
        <MatrixHeader numCols={numCols} onRemoveColumn={onRemoveColumn} />
        <TableBody>
          {matrix.map((row, rowIndex) => (
            <MatrixRow
              key={rowIndex}
              row={row}
              rowIndex={rowIndex}
              rowSum={calculateRowSum(row)}
              hoveredSumRowIndex={hoveredSumRowIndex}
              nearestCells={nearestCells}
              onCellClick={handleCellClick}
              onCellMouseEnter={handleCellMouseEnter}
              onCellMouseLeave={handleCellMouseLeave}
              onRemoveCell={onRemoveCell}
              onRemoveRow={onRemoveRow}
            />
          ))}
          <TableRow>
            {Array.from({ length: numCols }, (_, colIndex) => (
              <TableCell key={colIndex}>Average</TableCell>
            ))}
            <TableCell />
            <TableCell />
          </TableRow>
        </TableBody>
        <TableFooter>
          <TableRow>
            {columnMedians.map((median, colIndex) => (
              <TableCell key={colIndex}>{median}</TableCell>
            ))}
            <TableCell>Median</TableCell>
          </TableRow>
        </TableFooter>
      </Table>
    </TableContainer>
  );
};

export default MatrixTable;
