// src/App.tsx (or wherever your state management is handled)
import React, { useState, useCallback } from 'react';
import MatrixTable from './components/MatrixTable/MatrixTable';
import { Cell } from './type/type';
import { generateMatrix } from './utils/generateMatrix';

const App: React.FC = () => {
  const [matrix, setMatrix] = useState<Cell[][]>(generateMatrix(5, 5));

  const handleCellUpdate = (rowIndex: number, colIndex: number, newAmount: number) => {
    setMatrix(prevMatrix => {
      const newMatrix = prevMatrix.map((row, rIdx) =>
        row.map((cell, cIdx) => {
          if (rIdx === rowIndex && cIdx === colIndex) {
            return { ...cell, amount: newAmount };
          }
          return cell;
        })
      );
      return newMatrix;
    });
  };

  const handleRemoveRow = (rowIndex: number) => {
    setMatrix(prevMatrix => prevMatrix.filter((_, index) => index !== rowIndex));
  };

  return (
    <div>
      <MatrixTable matrix={matrix} onCellUpdate={handleCellUpdate} onRemoveRow={handleRemoveRow} />
    </div>
  );
};

export default App;
